import React from 'react'
import { TextField, MenuItem, Grid, IconButton, InputAdornment } from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";


function Filters(props) {

    const [mobile, setMobile] = React.useState('')

    const handleSelectChange = (e) => {
        props.setParams( state => ({...state, [e.target.name]: e.target.value}) );
    }

    React.useEffect( () => {
        if (mobile === ''){
            props.setParams( state => state)
        }
    }, [mobile])

    return (
        <Grid className="mb-3 ps-3" justify="flex-end" container spacing={1}>
            <Grid item xs={3}>
                <label className="text-white ms-2 mt-2">دسته‌بندی براساس</label>
                <TextField name="ave" value={1} defaultValue="1" onChange={handleSelectChange} style={{minWidth: "150px"}} select size="small">
                    <MenuItem value="1">همه کاربران</MenuItem>
                    <MenuItem value="2">2</MenuItem>
                </TextField>    
            </Grid>
            <Grid item ><TextField 
                            value={mobile} 
                            type="search" 
                            onChange={ e => setMobile(e.target.value)} 
                            name="mobile__contain" 
                            size="small" 
                            placeholder="جست و جو"
                            InputProps={{
                                endAdornment: (
                                  <InputAdornment>
                                    <IconButton onClick={ () => props.setParams( state => ({...state, mobile__contain: mobile}))}>
                                      <SearchIcon />
                                    </IconButton>
                                  </InputAdornment>
                                )
                              }}
                        />
            </Grid>
        </Grid>
    )
}

export default Filters
