import React, { PureComponent, useState } from 'react';
import { AreaChart, Area, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Legend } from 'recharts';
import '../layout/style.scss'

const data = [
    {
        name: 'Page A',
        uv: 4000,
        pv: 2400,
        amt: 2400,
    },
    {
        name: 'Page B',
        uv: 3000,
        pv: 1398,
        amt: 2210,
    },
    {
        name: 'Page C',
        uv: 2000,
        pv: 9800,
        amt: 2290,
    },
    {
        name: 'Page D',
        uv: 2780,
        pv: 3908,
        amt: 2000,
    },
    {
        name: 'Page E',
        uv: 1890,
        pv: 4800,
        amt: 2181,
    },
    {
        name: 'Page F',
        uv: 2390,
        pv: 3800,
        amt: 2500,
    },
    {
        name: 'Page G',
        uv: 3490,
        pv: 4300,
        amt: 2100,
    },
];
const label=[
    {key:"uv",color:"#558EFA"},
    {key:"pv",color:"#FFAA004D"},
    {key:"amt",color:"#26A17B"}
]
const renderLegend = (props) => {
    const { payload } = props;
    console.log("payload",payload)

    return (
        <div className='d-flex legendContainer'>
        {
            payload.map((entry, index) => (
                <div className='legendItem'>
                <div className='dot mx-auto' style={{backgroundColor:`${entry.color}`}}></div>
                <div key={`item-${index}`}>{entry.value}</div>
                <span>x</span>
            </div>
          ))
        }
      </div>
    );
  }

const NumOfOrders = () => {
    const [barProps, setBarProps] = useState(
        label.reduce(
          (a, { key }) => {
            a[key] = false;
            return a;
          },
          { hover: null }
        )
      );
      const handleLegendMouseEnter = (e) => {
        if (!barProps[e.dataKey]) {
          setBarProps({ ...barProps, hover: e.dataKey });
        }
      };

      const handleLegendMouseLeave = (e) => {
        setBarProps({ ...barProps, hover: null });
      };

      const selectBar = (e) => {
        setBarProps({
          ...barProps,
          [e.dataKey]: !barProps[e.dataKey],
          hover: null
        });
      };
        return (
            <ResponsiveContainer width={1000} height="90%">
                <AreaChart width={1000} height={250} data={data}
                    margin={{ top: 60, right: 30, left: -30, bottom: 0 }}>
                <Legend align="right" verticalAlign="top" height={30} iconType="circle" content={renderLegend}           
                onClick={selectBar}
                onMouseOver={handleLegendMouseEnter}
                onMouseOut={handleLegendMouseLeave}/>
                    <defs>
                        <linearGradient id="uv" x1="0" y1="0" x2="0" y2="1">
                            <stop offset="5%" stopColor="#558EFA" stopOpacity={0.8} />
                            <stop offset="95%" stopColor="#558EFA" stopOpacity={0} />
                        </linearGradient>
                        <linearGradient id="pv" x1="0" y1="0" x2="0" y2="1">
                            <stop offset="5%" stopColor="#FFAA004D" stopOpacity={0.8} />
                            <stop offset="95%" stopColor="#FFAA000D" stopOpacity={0} />
                        </linearGradient>
                        <linearGradient id="amt" x1="0" y1="0" x2="0" y2="1">
                            <stop offset="5%" stopColor="#26A17B" stopOpacity={0.8} />
                            <stop offset="95%" stopColor="#FFFFFF" stopOpacity={0} />
                        </linearGradient>
                    </defs>
                    <XAxis dataKey="name" />
                    <YAxis />
                    <CartesianGrid strokeDasharray="2 2" vertical={false} horizontal={true} />
                    <Tooltip />
                    {label.map((label,index) => (
                        <Area type="monotone" dataKey={label.key} hide={barProps[label.key] === true} stroke={label.color} fillOpacity={1} fill={`url(#${label.key})`} />
                    ))}
                    {/* <Area type="monotone" dataKey="uv" hide={barProps[label.key] === true} stroke="#558EFA" fillOpacity={1} fill="url(#colorUv)" />
                    <Area type="monotone" dataKey="pv" hide={barProps[label.key] === true} stroke="#FFAA004D" fillOpacity={1} fill="url(#colorPv)" />
                    <Area type="monotone" dataKey="amt" hide={barProps[label.key] === true} stroke="#26A17B" fillOpacity={1} fill="url(#colorAmt)" /> */}
                </AreaChart>
            </ResponsiveContainer>
        );

}

export default NumOfOrders;
