import React from 'react';
import Editor from './editor';
import { Typography, Container, Box } from '@material-ui/core';
import { createReactEditorJS } from "react-editor-js";
import { EDITOR_JS_TOOLS } from './editor/constants';
import Button from '@restart/ui/esm/Button';
import { IAM_APP , TERMS_AND_CONDITIONS_ITEM , TERMS_AND_CONDITIONS_UPDATE} from '../../utils/constants';
import { useFetchApi } from '../../utils/hooks';
import { UrlQuery } from '../../utils/utils';


const ReactEditorJS = createReactEditorJS();


const AboutUs = () => {    
    const [{ data: aboutUs, isLoading: aboutUsLoading },doFetch] = useFetchApi(undefined)
    const [{ data: aboutUsUpdate, isLoading: aboutUsUpdateLoading }, getAboutUs] = useFetchApi(undefined, {})

    const editorJS = React.useRef(null)

const handleInitialize = React.useCallback((instance) => {
  editorJS.current = instance
}, [])

// const handleSave = React.useCallback(() => {
//   const savedData =  editorJS.current.save();
// }, [])
async function handleSave() {
    const savedData = await editorJS.current.save()
    console.log("saved",savedData)
    const payload = {
        "key": "string",
        "value": JSON.stringify(savedData?.blocks)

      }
    getAboutUs("PUT", IAM_APP, TERMS_AND_CONDITIONS_UPDATE(11),payload)
   }



   React.useEffect( () => {
    doFetch("GET", IAM_APP, TERMS_AND_CONDITIONS_ITEM(11) )
}, [])

    const data = aboutUs?.value
    // const string = JSON.parse(data)  
    console.log("about us",savedData?.blocks)

    return (
        <div className="content">
        <div className=" d-flex align-items-center justify-content-between w-auto mx-auto pt-2">
          <div className="d-flex">
            <img src="/assests/drawer/Polygon.svg" />
            <p className="font_title_name me-1" style={{minWidth: "150px"}}>صفحه درباره ما</p>
          </div>
            </div>
            <Container
        style={{  minHeight: "100vh" }}
        maxWidth="xl">
        <Box p={5}>
          {/* <Box>
            <Typography variant="h6" component="span">
              صفحه درباره ما
        </Typography>
          </Box> */}
          <Box
            mt={2}
            style={{
              backgroundColor: '#ffffff',
              border: '1px solid #cccccc'
            }}>
            {/* <Editor 
            header_text="درباره ما"
            /> */}
            <ReactEditorJS
                tools={EDITOR_JS_TOOLS}
                onInitialize={handleInitialize}
                // onChange={() => handleSave()}
                defaultValue={{
                    time: 1635603431943,
                    blocks: [
                      {
                        id: "sheNwCUP5A",
                        type: "header",
                        data: {
                          text: "ادیت صفحه درباره ما",
                          level: 2
                        }
                      },            {
                        id: "12iM3lqzcm",
                        type: "paragraph",
                        data: {
                          text:
                            "موز (Moz) یک شرکت معروف در بهینه‌سازی موتور جستجو است که دارای یک وبلاگ بسیار خوب و حتی مجموعه‌ای از محصولات عالی است که کمک می‌کند به شما ترافیک بیشتری را برای وبسایت خود بدست آورید. صفحه درباره ما آنها یکی از نمونه‌های قوی می‌باشد که تمرکز اصلی صفحه درباره ما Moz یک گرافیک timeline (نمایش وقایع در یک بازه معین) است که به شما تاریخچه‌ای از گذشته و جزئیاتی در مورد دستاوردها، چالش‌ها و تاریخ‌های مهم شرکت را می‌دهد و می‌توانید تصاویر دایره‌ای را در طول مسیر، براحتی بررسی کنید. این امر به شما یک حس واقعی از ثبات و پایداری شرکت را می‌دهد و به شما القا می‌کند که Moz می‌تواند تا سال‌های طولانی در کنار شما باشد. مزیت عمده این نوع صفحه «درباره ما» این است که این نوار زمان (timeline) به شما این امکان را می‌دهد که حس کنید بخشی از داستان آنها هستید و می‌دانید که دقیقا چگونه شرکت شکوفا شده است و چه کارهایی در آن انجام شده است."
                        }
                      },]
                    }}
            />
          </Box>
        </Box>
        <Button onClick={() => handleSave()}>submit</Button>
      </Container>
        </div>
    );
}

export default AboutUs;
