import React from 'react';

import { makeStyles } from '@material-ui/styles';

import { Button } from '@material-ui/core';

import { DataGrid, useGridSlotComponentProps } from '@material-ui/data-grid';

import { Pagination } from '@material-ui/lab';

import { useFetchApi } from "../../../utils/hooks";

import { ADMIN_BASE_URL, IAM_APP, TRADING_APP, USERS_LIST, USERS_TRADES } from '../../../utils/constants';

import { timeToStr, UrlQuery } from '../../../utils/utils';

import { useHistory, useParams } from 'react-router-dom';

import Filters from './filters';

import { round, round_hundred_thousand } from '../../../utils/math-round'

import { numDiscriminant } from '../../../utils/discriminant'



const useStyles = makeStyles({

  root: {

    // display: 'flex',

    margin: '0 auto',

  },

});



function CustomPagination() {

  const { state, apiRef } = useGridSlotComponentProps();

  const classes = useStyles();



  return (

    <Pagination

      className={classes.root}

      shape="rounded"

      color="primary"

      // variant="contained"

      count={state.pagination.pageCount}

      page={state.pagination.page + 1}

      onChange={(event, value) => apiRef.current.setPage(value - 1)}

    />

  );

}



function TradesHistoryDatagrid(props) {



  const history = useHistory()



  const columns = [

    {

      field: 'tracking_code',

      headerName: 'شناسه سفارش',

      flex: 1,

      type: 'string',

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.tracking_code.slice(0, 8)}`

    },

    {

      field: 'market',

      headerName: 'بازار',

      flex: 0.5,

      type: 'string',

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.buy_order.dest_wallet}-${params.row?.buy_order.origin_wallet}`

    },

    {

      field: 'type',

      headerName: 'سمت',

      flex: 0.5,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => (

        <div className="d-flex">

          {console.log("params", params)}

          {params.row?.type === "sell" ? <span className="text-danger">فروش</span> : <span className="text-success">خرید</span>}

        </div>

      )

    },

    {

      field: 'dest_wallet',

      headerName: 'نوع سفارش',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.match_type === "limit" ? "محدود" : "سریع"}`

    },

    {

      field: 'user_name',

      headerName: 'نام کاربر',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.profile ? params.row?.profile[0]?.credentials.first_name ? params.row?.profile[0]?.credentials.first_name : "-" : null} ${params.row?.profile ? params.row?.profile[0]?.credentials.last_name ? params.row?.profile[0]?.credentials.last_name : "-" : null}`



    },

    {

      field: 'user_nationalcode',

      headerName: 'کد ملی',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.profile ? params.row?.profile[0]?.credentials.national_code ? params.row?.profile[0]?.credentials.national_code : "-" : null} `



    },

    {

      field: 'amount',

      headerName: 'قیمت سفارش',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (params) => `${numDiscriminant(round(params.row?.amount))} ${params.row?.buy_order.origin_wallet === "irr" ? "تومان" : "تتر"}`



    },

    {

      field: 'quantity',

      headerName: 'مقدار',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 0.5,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (params) => `${numDiscriminant(params.row?.quantity)}`



    },

    {

      field: 'total',

      headerName: 'مبلغ کل(تومان)',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (params) => `${(params?.row?.amount) * (params?.row?.quantity) == "0" ? "0" : numDiscriminant(round((params?.row?.amount) * (params?.row?.quantity)))}`

    },

    {

