// import "./styles.css";
import React, { useCallback, useState } from "react";
import { PieChart, Pie, Cell, Legend } from "recharts";

const renderLegend = (props) => {
    const { payload } = props;

    return (
      <ul>
        {
          payload.map((entry, index) => (
            <li key={`item-${index}`} style={{color:`${entry.color}`}}>
                {console.log("value",entry)}
                {entry.payload.name}
                {entry.payload.quantity}

            </li>
          ))
        }
      </ul>
    );
  }
const data = [
  { name: "Group A", value: 400 },
  { name: "Group B", value: 300 },
  { name: "Group C", value: 300 },
  { name: "Group D", value: 200 }
];

const COLORS = ["#0088FE", "#00C49F", "#FFBB28", "#FF8042"];

const RADIAN = Math.PI / 180;
const renderCustomizedLabel = ({
  cx,
  cy,
  midAngle,
  innerRadius,
  outerRadius,
  percent,
  index
}) => {
  const radius = innerRadius + (outerRadius - innerRadius) * 0.7;
  const x = cx + radius * Math.cos(-midAngle * RADIAN);
  const y = cy + radius * Math.sin(-midAngle * RADIAN);

  return (
    <text
      x={x}
      y={y}
      fill="white"
      textAnchor={x > cx ? "start" : "end"}
      dominantBaseline="central"
    >
      {`${(percent * 100).toFixed(0)}%`}
    </text>
  );
};
export default function WalletChart({chartValue}) {
  return (
    <PieChart width={268} height={350}>
    <Legend align="right" verticalAlign="middle" height={80} content={renderLegend} />
      <Pie
        data={chartValue.length && chartValue}
        cx={134}
        cy={134}
        labelLine={false}
        label={renderCustomizedLabel}
        outerRadius={100}
        innerRadius={60}
        fill="#8884d8"
        dataKey="value"
      >
        {data.map((entry, index) => (
          <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
        ))}
      </Pie>
    </PieChart>
  );
}
