import React from 'react';

import { makeStyles } from '@material-ui/styles';

import { Button } from '@material-ui/core';

import { DataGrid, useGridSlotComponentProps } from '@mui/x-data-grid';



// import { DataGrid, useGridSlotComponentProps } from '@material-ui/data-grid';

import { Pagination } from '@material-ui/lab';

import { useFetchApi } from "../../../utils/hooks";

import { USERS_TRANSACTION_HISTORY } from '../../../utils/constants';

import { UrlQuery } from '../../../utils/utils';

import { useHistory, useParams } from 'react-router-dom';

import Filters from './filters';

import { ACCOUNTING_APP, BASE_URL, WITHDRAWS_LIST, WITHDRAW_DETAIL } from '../../../utils/constants';







const useStyles = makeStyles({

  root: {

    // display: 'flex',

    margin: '0 auto',

  },

});



function CustomPagination() {

  const { state, apiRef } = useGridSlotComponentProps();

  const classes = useStyles();





  return (

    <Pagination

      className={classes.root}

      shape="rounded"

      color="primary"

      // variant="contained"

      count={state.pagination.pageCount}

      page={state.pagination.page + 1}

      onChange={(event, value) => apiRef.current.setPage(value - 1)}

    />

  );

}



const rows = [

  {

    id: 1,

    tracking_code: "412515",

    owner_id: "fjasfkljas",

    national_code: "55457575",

    origin_wallet: "btc",

    quantity: "2.3",

    dest_wallet: "",

    action: "approve",

    status: "in_progress",

  },

  {

    id: 2,

    tracking_code: "412515",

    owner_id: "fjasfkljas",

    national_code: "55457575",

    origin_wallet: "btc",

    quantity: "2.3",

    dest_wallet: "",

    action: "approve",

    status: "in_progress",

  },

  {

    id: 3,

    tracking_code: "412515",

    owner_id: "fjasfkljas",

    national_code: "55457575",

    origin_wallet: "btc",

    quantity: "2.3",

    dest_wallet: "",

    action: "approve",

    status: "in_progress",

  },

]



function CryptoAccountantDatagrid(props) {



  const history = useHistory()





  const columns = [

    {

      field: 'tracking_code',

      headerName: 'شناسه',

      flex: 1,

      type: 'string',

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => `${values.row?.tracking_code.slice(0, 8)}`

    },

    {

      field: 'owner_id',

      headerName: 'نام کاربر',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => `${values.row?.user[0]?.credentials.first_name} ${values.row?.user[0]?.credentials.last_name}`

      // renderCell: (values) => (

      //   <div className="d-flex">

      //        {values.row?.trade_type === "limit" ? "محدود" : "سریع"}

      //   </div>

      // )

    },

    {

      field: 'national_code',

      headerName: 'کدملی',

      flex: 0.75,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => `${values.row?.user[0]?.credentials.national_code}`

      // renderCell: (values) => (

      //   <div className="d-flex">

      //        {values.row?.trade_type === "sell" ? <span className="text-danger">فروش</span> : <span className="text-success">خرید</span>}

      //   </div>

      // )

    },

    {

      field: 'origin_wallet',

      headerName: 'بازار',

      flex: .5,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => `${values.row?.wallet?.c_type}`

      // renderCell: (values) => (

      //   <div className="d-flex">

      //        <span>{values.row?.origin_wallet} - {values.row?.dest_wallet}</span>

      //   </div>

      // )

    },

    {

      field: 'quantity',

      headerName: 'مقدار برداشت',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

    },

    {

      field: 'commission_percentage',

      headerName: 'کیف پول مقصد',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 2,

      headerClassName: 'grid-header',

      headerAlign: 'center',

    },

    {

      field: 'created_at',

      headerName: 'تاریخ',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1.5,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => (

        <>

          <Button variant="contained" color="secondary" onClick={() => handleAction("in_progress", values.row.id)} small className="ms-4">تایید برداشت</Button>

          <Button variant="contained" onClick={() => handleAction("rejected", values.row.id)} small className="bg-danger text-white">لغو</Button>

        </>

      )

    },

    {

      field: 'status',

      headerName: 'وضعیت',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => <div className="d-flex justify-content-end w-100">

        {

          values?.row?.status === "in_progress" ? (<Button variant="contained" color="primary" onClick={() => { }} className="ms-2">تایید نهایی</Button>)

            : (

              values?.row?.status === "success" ? '' : ''

            )

        }

      </div>

    }

  ];



  // const [params, setParams] = React.useState({order: "-updated_at", size: 10, page: 0})

  // const [{ data, isLoading, hasError }, doFetch] = useFetchApi(undefined, [])



  //   React.useEffect( () => {

  //       // doFetch("GET", UrlQuery(USERS_TRANSACTION_HISTORY, {...params}) )

  //   }, [params])

  const [params, setParams] = React.useState({ order: "-updated_at", size: 10, page: 0 })

  const [{ data, isLoading, hasError }, doFetch] = useFetchApi(undefined, {});

  const [{ data: withdrawData, isLoading: withdrawLoading }, updateWithdraw] = useFetchApi(undefined, {});

  const updateData = () => {

    data?.results?.map((item, index) => {

      let update = data?.users_info?.filter((itm, idx) => idx === index)

      console.log("update", update);

      return item.user = update

    })

  }

  const handleAction = (action, withdrawId) => {

    if (["success", "rejected", "in_progress"].includes(action)) {

      // console.log(action, withdrawId);

      updateWithdraw("PUT", ACCOUNTING_APP, WITHDRAW_DETAIL(withdrawId), { status: action })

      setTimeout(() => doFetch("GET", ACCOUNTING_APP, UrlQuery(WITHDRAWS_LIST, { ...params })), 1500)

    }

  }

  React.useEffect(() => {

    doFetch("GET", ACCOUNTING_APP, UrlQuery(WITHDRAWS_LIST, { ...params }))

  }, [params])


  React.useEffect(() => {

    updateData()

  }, [data])

  return (

    <div className="content">

      <div className=" d-flex align-items-center justify-content-between w-auto mx-auto pt-2">

        <div className="d-flex">

          <img src="/assests/drawer/Polygon.svg" />

          <p className="font_title_name me-1" style={{ minWidth: "150px" }}>تایید برداشت ارز</p>

        </div>

        <Filters params={params} setParams={setParams} />

      </div>

      <div className="d-flex px-3">

        <div style={{ flexGrow: 1, width: "100%", height: "100%" }}>

          <DataGrid

            style={{ border: 0 }}

            autoHeight

            rows={data?.results ?? []}

            // rows={rows}

            loading={isLoading}

            columns={columns}

            getRowClassName={

              params => 'grid-content'

            }

            headerHeight={50}

            rowHeight={52}

            isRowSelectable={false}

            components={{

              Pagination: CustomPagination

            }}

            pageSize={10}

            rowCount={data?.results?.length}

            paginationMode="server"

            onPageChange={(param) => doFetch("GET", UrlQuery(USERS_TRANSACTION_HISTORY, { ...params, page: param }))}

            disableSelectionOnClick

            disableColumnMenu

          />

        </div>

      </div>

    </div>

  )

}



export default CryptoAccountantDatagrid
