import React from 'react';

import { makeStyles } from '@material-ui/styles';

import { Button } from '@material-ui/core';

import { DataGrid, useGridSlotComponentProps } from '@material-ui/data-grid';

import { Pagination } from '@material-ui/lab';

import { useFetchApi } from "../../../utils/hooks";

import { ACCOUNTING_APP, ADMIN_BASE_URL, USERS_TRANSACTION_HISTORY } from '../../../utils/constants';

import { timeToStr, UrlQuery } from '../../../utils/utils';

import { useHistory, useParams } from 'react-router-dom';

import Filters from './filters';





const useStyles = makeStyles({

  root: {

    // display: 'flex',

    margin: '0 auto',

  },

});



function CustomPagination() {

  const { state, apiRef } = useGridSlotComponentProps();

  const classes = useStyles();



  return (

    <Pagination

      className={classes.root}

      shape="rounded"

      color="primary"

      // variant="contained"

      count={state.pagination.pageCount}

      page={state.pagination.page + 1}

      onChange={(event, value) => apiRef.current.setPage(value - 1)}

    />

  );

}



function HistoryAccountantDatagrid(props) {



  const history = useHistory()



  const columns = [

    {

      field: 'tracking_code',

      headerName: 'شناسه تراکنش',

      flex: 1,

      type: 'string',

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => `${values.row?.tracking_code.slice(0, 8)}`

    },

    {

      field: 'created_at',

      headerName: 'تاریخ و ساعت',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      valueFormatter: data => timeToStr(data.value, "HH:mm  -   jYYYY/jMM/jDD")

    },

    {

      field: 'type',

      headerName: 'نوع عملیات',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => {

        let className = ''

        let text = ''

        switch (values.row?.order_type) {

          case "trade":

            switch (values.row?.type) {

              case "increase":

                className = "text-success"

                text = "خرید"

                break;

              case "decrease":

                className = "text-danger"

                text = "فروش"

                break;
            }

            break;

            case "withdraw":

              className = "text-danger"

              text = "برداشت"

            break;

            case "deposit":

              className = "text-danger"

              text = "واریز"

            break;

            case "payment_gateway":

              switch (values.row?.type) {

                case "increase":

                  className = "text-success"

                  text = "واریز"

                  break;

                case "decrease":

                  className = "text-danger"

                  text = "برداشت"

                  break;
              }

            break;

          default:

            break;

        }

        return <div className="d-flex">

          <span className={className}>{text}</span>

        </div>

      }

    },

    {

      field: 'owner_id',

      headerName: 'نام کاربر',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      // renderCell: (values) => `${values.row?.user[0]?.credentials.first_name} ${values.row?.user[0]?.credentials.last_name}`

      // renderCell: (values) => (

      //   <div className="d-flex">

      //        {values.row?.trade_type === "limit" ? "محدود" : "سریع"}

      //   </div>

      // )

    },

    {

      field: 'quantity_with_fee',

      headerName: 'مبلغ تراکنش(تومان)',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 0.5,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => `${values.row?.quantity_with_fee}`


    },

    {

      field: 'commission_id',

      headerName: 'کارمزد(تومان)',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 0.5,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => `${values.row?.commission}`


    },

    {

      field: 'track_code',

      headerName: 'کد پیگیری',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => `${values.row?.tracking_code}`

    },
    {

      field: 'description',

      headerName: 'توضیحات',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => {

        let className = ''

        let text = ''

        switch (values.row?.order_type) {

          case "trade":

            switch (values.row?.type) {

              case "increase":


                text = "خرید"

                break;

              case "decrease":


                text = "فروش"

                break;
            }

            break;

            case "withdraw":

              text = "برداشت از کیف پول"

            break;

            case "deposit":

              text = "شارژ کیف پول"

            break;

            case "payment_gateway":

              switch (values.row?.type) {

                case "increase":


                  text = "شارژ کیف پول"

                  break;

                case "decrease":


                  text = "برداشت از کیف پول"

                  break;
              }

            break;

          default:

            break;

        }

        return <div className="d-flex">

          <span>{text}</span>

        </div>

      }

    },


    {

      field: 'status',

      headerName: 'وضعیت',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => {

        let text = values.row?.status === "success" ? "موفق" : "ناموفق"

        let pic = values.row?.status === "success" ? "/assests/drawer/check_circle-24px.svg" : "/assests/drawer/cancel-24px (1).svg"

        return <div className="d-flex justify-content-center w-100">

          {text}

          <img src={pic} />

        </div>

      }

    },

  ];



  const [params, setParams] = React.useState({ order: "-updated_at", size: 10, page: 0 })

  const [{ data, isLoading, hasError }, doFetch] = useFetchApi(undefined, [])



  React.useEffect(() => {

    doFetch("GET", ACCOUNTING_APP, UrlQuery(USERS_TRANSACTION_HISTORY, { ...params }))

  }, [params])



  return (

    <div className="content">

      <div className=" d-flex align-items-center justify-content-between w-auto mx-auto pt-2">

        <div className="d-flex">

          <img src="/assests/drawer/Polygon.svg" />

          <p className="font_title_name me-1" style={{ minWidth: "150px" }}>تاریخچه تراکنش‌ها </p>

        </div>

        <Filters params={params} setParams={setParams} />

      </div>

      <div className="d-flex px-3">

        <div style={{ flexGrow: 1, width: "100%", height: "100%" }}>

          <DataGrid

            style={{ border: 0 }}

            autoHeight

            rows={data?.items ?? []}

            loading={isLoading}

            columns={columns}

            getRowClassName={

              params => 'grid-content'

            }

            headerHeight={50}

            rowHeight={52}

            isRowSelectable={false}

            components={{

              Pagination: CustomPagination

            }}

            pageSize={10}

            rowCount={data?.total}

            paginationMode="server"

            onPageChange={(param) => doFetch("GET", ACCOUNTING_APP, UrlQuery(USERS_TRANSACTION_HISTORY, { ...params, page: param }))}

            disableSelectionOnClick

            disableColumnMenu

          />

        </div>

      </div>

    </div>

  )

}



export default HistoryAccountantDatagrid
