import React from "react";
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend
} from "recharts";

const data = [
  {
    date: "1399-01",
    uv: 4000,
    pv: 2400,
    amt: 2400
  },
  {
    date: "1399-02",
    uv: 3000,
    pv: 1398,
    amt: 2210
  },
  {
    date: "1399-03",
    uv: 2000,
    pv: 9800,
    amt: 2290
  },
  {
    date: "1399-04",
    uv: 2780,
    pv: 3908,
    amt: 2000
