    pv: 4800,
    amt: 2181
  },
  {
    date: "1399-06",
    uv: 2390,
    pv: 3800,
    amt: 2500
  },
  {
    date: "1399-07",
    uv: 3490,
    pv: 4300,
    amt: 2100
  },
  {
    date: "1399-08",
    uv: 4000,
    pv: 2400,
    amt: 2400
  },
  {
    date: "1399-09",
    uv: 3000,
    pv: 1398,
    amt: 2210
  },
  {
    date: "1399-10",
    uv: 2000,
    pv: 9800,
    amt: 2290
  },
  {
    date: "1399-11",
    uv: 2780,
    pv: 3908,
    amt: 2000
  },
  {
    date: "1399-12",
    uv: 1890,
    pv: 4800,
    amt: 2181
  }
];

const monthTickFormatter = (tick) => {
  const date = new Date(tick);

  return date.getMonth() + 1;
};

// const renderQuarterTick = (tickProps) => {
