import React from 'react';

import { makeStyles } from '@material-ui/styles';

import { Button } from '@material-ui/core';

import { DataGrid, useGridSlotComponentProps } from '@material-ui/data-grid';

import { Pagination } from '@material-ui/lab';

import { useFetchApi } from "../../../utils/hooks";

import { ADMIN_BASE_URL, IAM_APP, TRADING_APP, USERS_LIST, USERS_TRADES } from '../../../utils/constants';

import { timeToStr, UrlQuery } from '../../../utils/utils';

import { useHistory, useParams } from 'react-router-dom';

import Filters from './filters';

import { round, round_hundred_thousand } from '../../../utils/math-round'

import { numDiscriminant } from '../../../utils/discriminant'



const useStyles = makeStyles({

  root: {

    // display: 'flex',

    margin: '0 auto',

  },

});



function CustomPagination() {

  const { state, apiRef } = useGridSlotComponentProps();

  const classes = useStyles();



  return (

    <Pagination

      className={classes.root}

      shape="rounded"

      color="primary"

      // variant="contained"

      count={state.pagination.pageCount}

      page={state.pagination.page + 1}

      onChange={(event, value) => apiRef.current.setPage(value - 1)}

    />

  );

}



function TradesHistoryDatagrid(props) {



  const history = useHistory()



  const columns = [

    {

      field: 'tracking_code',

      headerName: 'شناسه سفارش',

      flex: 1,

      type: 'string',

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.tracking_code.slice(0, 8)}`

    },

    {

      field: 'market',

      headerName: 'بازار',

      flex: 0.5,

      type: 'string',

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.buy_order.dest_wallet}-${params.row?.buy_order.origin_wallet}`

    },

    {

      field: 'type',

      headerName: 'سمت',

      flex: 0.5,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => (

        <div className="d-flex">

          {console.log("params", params)}

          {params.row?.trade_type === "sell" ? <span className="text-danger">فروش</span> : <span className="text-success">خرید</span>}

        </div>

      )

    },

    {

      field: 'dest_wallet',

      headerName: 'نوع سفارش',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.match_type === "limit" ? "محدود" : "سریع"}`

    },

    {

      field: 'user_name',

      headerName: 'نام کاربر',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.profile ? params.row?.profile[0]?.credentials.first_name ? params.row?.profile[0]?.credentials.first_name : "-" : null} ${params.row?.profile ? params.row?.profile[0]?.credentials.last_name ? params.row?.profile[0]?.credentials.last_name : "-" : null}`



    },

    {

      field: 'user_nationalcode',

      headerName: 'کد ملی',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (params) => `${params.row?.profile ? params.row?.profile[0]?.credentials.national_code ? params.row?.profile[0]?.credentials.national_code : "-" : null} `



    },

    {

      field: 'amount',

      headerName: 'قیمت سفارش',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (params) => `${round(params.row?.amount)} ${params.row?.buy_order.origin_wallet === "irr" ? "تومان" : "تتر"}`



    },

    {

      field: 'quantity',

      headerName: 'مقدار',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 0.5,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (params) => `${params.row?.quantity}`



    },

    {

      field: 'total',

      headerName: 'مبلغ کل(تومان)',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (params) => `${round((params?.row?.amount) * (params?.row?.quantity))}`

    },

    {

      field: 'commission',

      headerName: 'کارمزد',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 0.5,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (params) => {

        let commission = params.row?.type === "buy" ? params.row?.buy_order.commission_percentage : params.row?.sell_order.commission_percentage

        let destWalletName = params.row?.type === "buy" ? params.row?.buy_order.dest_wallet : params.row?.sell_order.dest_wallet

        return `${numDiscriminant(round_hundred_thousand(commission * params.row?.quantity))} ${destWalletName}`

      }

    },

    {

      field: 'income',

      headerName: 'دریافتی',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (params) => {

        let commission = params.row?.type === "buy" ? params.row?.buy_order.commission_percentage : params.row?.sell_order.commission_percentage

        let destWalletName = params.row?.type === "buy" ? params.row?.buy_order.dest_wallet : params.row?.sell_order.dest_wallet

        let originWallet = params.row?.buy_order.origin_wallet === "btc" ? " بیتکوین" : params.row?.buy_order.origin_wallet === "usdt" ? "تتر" : params.row?.buy_order.origin_wallet === "irr" ? "تومان" : params.row?.buy_order.origin_wallet === "eth" ? "اتریوم" : params.row?.buy_order.origin_wallet === "trx" ? "ترون" : params.row?.buy_order.origin_wallet === "eos" ? "ایاس" : params.row?.buy_order.origin_wallet === "ada" ? "آدا" : params.row?.buy_order.origin_wallet === "xlm" ? "استلار" : params.row?.buy_order.origin_wallet === "ltc" ? "لایت کوین" : params.row?.buy_order.origin_wallet === "bnb" ? "بایننس کوین" : params.row?.buy_order.origin_wallet === "doge" ? "دوج" : params.row?.buy_order.origin_wallet === "bch" ? "بیتکوین کش" : null

        return params.row?.trade_type === "sell" ? `${numDiscriminant(round_hundred_thousand(params.row?.quantity - (commission * params.row?.quantity)))} ${destWalletName}` : `${numDiscriminant(round_hundred_thousand(params.row?.amount - (commission * params.row?.amount)))} ${originWallet}`

        // {type === "buy" ? `${numDiscriminant(round_hundred_thousand(item.quantity - (commission * item.quantity)))} ${destWalletName}` : `${numDiscriminant(round_hundred_thousand(item.amount - (commission * item.amount)))} ${originWallet}`}

      }

    },

    {

      field: 'created_at',

      headerName: 'تاریخ',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      valueFormatter: data => timeToStr(data.value, "HH:mm  -   jYYYY/jMM/jDD")

    },

  ];



  const [params, setParams] = React.useState({ order: "-updated_at", size: 10, page: 0 })

  const [{ data: trades, isLoading: tradeIsLoading, hasError: tradeError }, doFetch] = useFetchApi(undefined, [])

  const [{ data: users, isLoading: usersIsLoading, hasError: usersError }, doFetchUser] = useFetchApi(undefined, [])



  const profile = () => {

    let user

    console.log("user", trades?.items)

    trades?.items?.map((item) => {

      user = users?.items?.filter(itm => itm.id === (item.type === "buy" ? item.buy_order.owner_id : item.sell_order.owner_id))

      return item.profile = user

    })

  }

  React.useEffect(() => {

    doFetch("GET", TRADING_APP, UrlQuery(USERS_TRADES, { ...params }))

    // doFetchUser("GET", IAM_APP, UrlQuery(USERS_LIST))

  }, [params])



  React.useEffect(() => {

    doFetchUser("GET", IAM_APP, USERS_LIST)

  }, [])







  React.useEffect(() => {

    profile()

  }, [users, params, trades])

  return (

    <div className="content">

      <div className=" d-flex align-items-center justify-content-between w-auto mx-auto pt-2">

        <div className="d-flex">

          <img src="/assests/drawer/Polygon.svg" />

          <p className="font_title_name me-1" style={{ minWidth: "150px" }}>تاریخچه معاملات </p>

        </div>

        <Filters params={params} setParams={setParams} />

      </div>

      <div className="d-flex px-0">

        <div style={{ flexGrow: 1, width: "100%", height: "100%" }}>

          <DataGrid

            style={{ border: 0 }}

            autoHeight

            rows={trades?.items ?? []}

            loading={tradeIsLoading}

            columns={columns}

            getRowClassName={

              params => 'grid-content'

            }

            headerHeight={50}

            rowHeight={52}

            isRowSelectable={false}

            components={{

              Pagination: CustomPagination

            }}

            pageSize={10}

            rowCount={trades?.total}

            paginationMode="server"

            onPageChange={(param) => doFetch("GET", TRADING_APP, UrlQuery(USERS_TRADES, { ...params, page: param.page }))}

            disableSelectionOnClick

            disableColumnMenu

          />

        </div>

      </div>

    </div>

  )

}



export default TradesHistoryDatagrid
