import React from 'react';

import { makeStyles } from '@material-ui/styles';

import { Button } from '@material-ui/core';

import { DataGrid, useGridSlotComponentProps } from '@material-ui/data-grid';

import { Pagination } from '@material-ui/lab';

import { useFetchApi } from "../../../utils/hooks";

import { ACCOUNTING_APP, BASE_URL, USERS_TRANSACTION_HISTORY, WITHDRAWS_LIST, WITHDRAW_DETAIL } from '../../../utils/constants';

import { UrlQuery } from '../../../utils/utils';

import { useHistory, useParams } from 'react-router-dom';

import Filters from './filters';





const useStyles = makeStyles({

  root: {

    // display: 'flex',

    margin: '0 auto',

  },

});



function CustomPagination() {

  const { state, apiRef } = useGridSlotComponentProps();

  const classes = useStyles();



  return (

    <Pagination

      className={classes.root}

      shape="rounded"

      color="primary"

      // variant="contained"

      count={state.pagination.pageCount}

      page={state.pagination.page + 1}

      onChange={(event, value) => apiRef.current.setPage(value - 1)}

    />

  );

}



const rows = [

  {

    id: 1,

    tracking_code: "412515",

    owner_id: "fjasfkljas",

    national_code: "55457575",

    origin_wallet: "btc",

    quantity: "2.3",

    dest_wallet: "",

    action: "approve",

    status: "in_progress",

  },

  {

    id: 2,

    tracking_code: "412515",

    owner_id: "fjasfkljas",

    national_code: "55457575",

    origin_wallet: "btc",

    quantity: "2.3",

    dest_wallet: "",

    action: "approve",

    status: "in_progress",

  },

  {

    id: 3,

    tracking_code: "412515",

    owner_id: "fjasfkljas",

    national_code: "55457575",

    origin_wallet: "btc",

    quantity: "2.3",

    dest_wallet: "",

    action: "approve",

    status: "in_progress",

  },

]



function RialAccountantDatagrid(props) {



  const history = useHistory()



  const columns = [

    {

      field: 'tracking_code',

      headerName: 'شناسه سفارش',

      flex: 1,

      type: 'string',

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => `${values.row?.tracking_code.slice(0, 8)}`

    },

    {

      field: 'user_id',

      headerName: 'نام کاربر',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => `${values.row?.user?values.row?.user[0]?.credentials.first_name:null} ${values.row?.user?values.row?.user[0]?.credentials.last_name:null}`


    },

    {

      field: 'national_code',

      headerName: 'کدملی',

      flex: 0.75,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      renderCell: (values) => (

        <div className="d-flex">

          {values.row?.credentials?.national_code}

        </div>

      )

    },

    {

      field: 'amount',

      headerName: 'مبلغ برداشت(تومان)',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

    },

    {

      field: 'description',

      headerName: 'شماره شبا واریز',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 2,

      headerClassName: 'grid-header',

      headerAlign: 'center',

    },

    {

      field: 'action',

      headerName: 'تایید یا لغو برداشت',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1.5,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => {

        if (values?.row.status === "pending") {

          return (

            <>

              <Button variant="contained" color="secondary" onClick={() => handleAction("in_progress", values.row.id)} small className="ms-4">تایید برداشت</Button>

              <Button variant="contained" onClick={() => handleAction("rejected", values.row.id)} small className="bg-danger text-white">لغو</Button>

            </>

          )

        } else if (values?.row.status === "success") {

          return (

            <div className="d-flex justify-content-center w-100">

              تایید شده

              <img src="/assests/drawer/check_circle-24px.svg" />

            </div>

          )

        } else if (values?.row?.status === "canceled") {

          return (

            <div className="d-flex justify-content-center w-100">

              لغو شده

              <img src="/assests/drawer/cancel-24px (1).svg" />

            </div>

          )

        } else if (values?.row?.status === "in_progress") {

          return (

            <div className="d-flex justify-content-center w-100">

              تایید شده

              <img src="/assests/drawer/cancel-24px (1).svg" />

            </div>

          )

        } else {

          return null

        }

      }

    },

    {

      field: 'status',

      headerName: 'وضعیت',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 1,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => {

        if (values?.row.status === "in_progress" || values?.row.status === "pending") {

          return (

            <Button variant="contained" color="primary" onClick={() => handleAction("success", values.row.id)} className="ms-2">تایید نهایی</Button>

          )

        } else if (values?.row.status === "success") {

          return (

            <div className="d-flex justify-content-center w-100">

              تسویه شده

              <img src="/assests/drawer/check_circle-24px.svg" />

            </div>

          )

        } else if (values?.row?.status === "rejected") {

          return (

            <div className="d-flex justify-content-center w-100">

              لغو شده

              <img src="/assests/drawer/cancel-24px (1).svg" />

            </div>

          )

        } else {

          return ""

        }

      }

    }

  ];



  const [params, setParams] = React.useState({ order: "-updated_at", crypto_type: 'irr', size: 10, page: 0 })

  const [{ data, isLoading, hasError }, doFetch] = useFetchApi(undefined, {});

  const [{ data: withdrawData, isLoading: withdrawLoading }, updateWithdraw] = useFetchApi(undefined, {});



  const [withdrawsList, setWithdrawsList] = React.useState([]);



  const handleAction = (action, withdrawId) => {

    if (["success", "rejected", "in_progress"].includes(action)) {

      // console.log(action, withdrawId);

      updateWithdraw("PUT", ACCOUNTING_APP, WITHDRAW_DETAIL(withdrawId), { status: action })

      setTimeout(() => doFetch("GET", ACCOUNTING_APP, UrlQuery(WITHDRAWS_LIST, { ...params })), 1500)

    }

  }
  const updateData = () => {

    data?.results?.map((item, index) => {

      let update = data?.users_info?.filter((itm, idx) => idx === index)

      console.log("update", update);

      return item.user = update

    })

  }


  React.useEffect(() => {

    doFetch("GET", ACCOUNTING_APP, UrlQuery(WITHDRAWS_LIST, { ...params }))

  }, [params])



  React.useEffect(() => {

    if (Object.keys(data).length) {

      let list = []

      for (let index = 0; index < data.results.length; index++) {

        list.push({ ...data.results[index], credentials: data.users_info[index].credentials })

      }

      setWithdrawsList(list)

    }

  }, [data])

  React.useEffect(() => {

    updateData()

  }, [data])

  console.log("data", data)


  return (

    <div className="content">

      <div className=" d-flex align-items-center justify-content-between w-auto mx-auto pt-2">

        <div className="d-flex">

          <img src="/assests/drawer/Polygon.svg" />

          <p className="font_title_name me-1" style={{ minWidth: "150px" }}>تایید برداشت ریال</p>

        </div>

        <Filters params={params} setParams={setParams} />

      </div>

      <div className="d-flex px-3">

        <div style={{ flexGrow: 1, width: "100%", height: "100%" }}>

          <DataGrid

            style={{ border: 0 }}

            autoHeight

            rows={withdrawsList}

            loading={isLoading || withdrawLoading}

            columns={columns}

            getRowClassName={

              params => 'grid-content'

            }

            headerHeight={50}

            rowHeight={52}

            isRowSelectable={false}

            components={{

              Pagination: CustomPagination

            }}

            pageSize={10}

            rowCount={data?.results?.total}

            paginationMode="server"

            onPageChange={(param) => doFetch("GET", ACCOUNTING_APP, UrlQuery(WITHDRAWS_LIST, { ...params, page: param }))}

            disableSelectionOnClick

            disableColumnMenu

          />

        </div>

      </div>

    </div>

  )

}



export default RialAccountantDatagrid
