import React, { useState } from 'react'

import {
    TextField, MenuItem, Grid,

    IconButton, InputAdornment, Select , withStyles

} from "@material-ui/core";

import InputBase from '@material-ui/core/InputBase';

import SearchIcon from "@material-ui/icons/Search";

import '../../layout/style.css';




function Filters(props) {



    const [tradeType, setTradeType] = useState('');

    const [originWallet, setOriginWallet] = useState('');

    const [nationalCode, setNationalCode] = React.useState('')



    const handleSelectChange = (e) => {

        props.setParams(state => ({ ...state, [e.target.name]: e.target.value }));

    }


    const BootstrapInput = withStyles((theme) => ({

        root: {

            //   'label + &': {

            //     marginTop: theme.spacing(3),

            //   },

        },

        input: {

            borderRadius: 4,

            position: 'relative',

            backgroundColor: 'white',

            border: '1px solid #ced4da',

            fontSize: 'smaller',

            padding: '5px 12px 5px 12px',

            color: 'black',

            transition: theme.transitions.create(['border-color', 'box-shadow']),

            // Use the system font instead of the default Roboto font.

            fontFamily: [

                '-apple-system',

                'BlinkMacSystemFont',

                '"Segoe UI"',

                'Roboto',

                '"Helvetica Neue"',

                'Arial',

                'sans-serif',

                '"Apple Color Emoji"',

                '"Segoe UI Emoji"',

                '"Segoe UI Symbol"',

            ].join(','),

            '&:focus': {

                borderRadius: 4,

                borderColor: '#80bdff',

                boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',

            },

        },

    }))(InputBase);


    return (

        <Grid className="mb-3 ps-4" justify="flex-end" container spacing={1}>

            <Grid item className="d-flex">

            <label className="text-white ms-2 mt-2" style={{ fontSize: 'smaller' }}>نوع ارز</label>

            <Select name="origin_wallet" value={originWallet} input={<BootstrapInput />} displayEmpty onChange={e => { handleSelectChange(e); setOriginWallet(e.target.value) }} select size="small">

                <MenuItem value="">همه ارزها</MenuItem>

                <MenuItem value="irr">ریال</MenuItem>

                <MenuItem value="btc">بیت کوین</MenuItem>

            </Select>

            </Grid>

            <Grid item className="d-flex">

                <label className="text-white ms-2 mt-2" style={{ fontSize: 'smaller' }}>نوع ارز</label>

                <Select name="origin_wallet" value={originWallet} input={<BootstrapInput />} displayEmpty onChange={e => { handleSelectChange(e); setOriginWallet(e.target.value) }} select size="small">

                    <MenuItem value="">همه ارزها</MenuItem>

                    <MenuItem value="irr">ریال</MenuItem>

                    <MenuItem value="btc">بیت کوین</MenuItem>

                </Select>

            </Grid>

            <Grid item className="mt-2" >

                <TextField

                    value={nationalCode}

                    type="search"

                    onChange={e => setNationalCode(e.target.value)}

                    name="national_code"

                    size="small"

                    placeholder="جست و جو"

                    InputProps={{

                        endAdornment: (

                            <InputAdornment>

                                <IconButton onClick={() => props.setParams(state => ({ ...state, national_code: nationalCode }))}>

                                    <SearchIcon />

                                </IconButton>

                            </InputAdornment>

                        )

                    }}

                />

            </Grid>

        </Grid>

    )

}



export default Filters
