import React, { useState } from 'react'

import {

    TextField, MenuItem, Grid,

    IconButton, InputAdornment, Select, withStyles

} from "@material-ui/core";

import SearchIcon from "@material-ui/icons/Search";

import InputBase from '@material-ui/core/InputBase';

import '../layout/style.css';

import { ConfigProvider } from 'antd';

import { DatePicker as DatePickerJalali } from "antd-jalali";

import fa_IR from "antd/lib/locale/fa_IR";

import 'antd/dist/antd.css';

import { UserContext } from '../../../context/provider';

function Filters(props) {

    const contextData = React.useContext(UserContext);

    const [tradeType, setTradeType] = useState('');

    const [originWallet, setOriginWallet] = useState('');

    const [nationalCode, setNationalCode] = useState('')

    const [dateString, setDateString] = useState();

    const dateFormat = 'YYYY-MM-DD';

        //date picker onchange func

    function onChange(date, dateString) {

        if (date) {

            props.setParams(state => ({ ...state,  date__gt: `${date[0].$y}-${date[0].$M + 1}-${date[0].$D}` , date__lt: `${date[1].$y}-${date[1].$M + 1}-${date[1].$D}`} ))

        }

    }

    const handleSelectChange = (e) => {

        props.setParams(state => ({ ...state, [e.target.name]: e.target.value }));

    }



    const BootstrapInput = withStyles((theme) => ({

        root: {

            //   'label + &': {

            //     marginTop: theme.spacing(3),

            //   },

        },

        input: {

            borderRadius: 4,

            position: 'relative',

            backgroundColor: 'white',

            border: '1px solid #ced4da',

            fontSize: 'smaller',

            padding: '5px 12px 5px 12px',

            color: 'black',

            transition: theme.transitions.create(['border-color', 'box-shadow']),

            // Use the system font instead of the default Roboto font.

            fontFamily: [

                '-apple-system',

                'BlinkMacSystemFont',

                '"Segoe UI"',

                'Roboto',

                '"Helvetica Neue"',

                'Arial',

                'sans-serif',

                '"Apple Color Emoji"',

                '"Segoe UI Emoji"',

                '"Segoe UI Symbol"',

            ].join(','),

            '&:focus': {

                borderRadius: 4,

                borderColor: '#80bdff',

                boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',

                backgroundColor: 'white'

            },

        },

    }))(InputBase);

    //   const [{ data, isLoading, hasError }, doFetch] = useFetchApi(undefined, [])

    //   React.useEffect( () => {
    //       doFetch("GET", ACCOUNTING_APP, USERS_WALLET(7) )

    //   }, [])
    //   console.log("wallet",data)
    return (
        <Grid className="mb-3 ps-2 position-relative" justify="flex-end" container spacing={1}>

            {contextData.depositFilter === "coin" ? 

            <Grid item className="d-flex">

                <label className="text-white ms-2 mt-2" style={{ fontSize: 'smaller' }}>نوع ارز</label>

                <Select name="origin_wallet" value={originWallet} input={<BootstrapInput />} displayEmpty onChange={e => { handleSelectChange(e); setOriginWallet(e.target.value) }} select size="small">

                    <MenuItem value="">همه ارزها</MenuItem>

                    <MenuItem value="irr">ریال</MenuItem>

                    <MenuItem value="btc">بیت کوین</MenuItem>

                </Select>

            </Grid>
            :
            null
            }

            {/* <Grid item className="d-flex">

                <label className="text-white ms-2 mt-2" style={{ fontSize: 'smaller' }}>سمت سفارش</label>

                <Select name="trade_type" value={tradeType} input={<BootstrapInput />} displayEmpty onChange={e => { handleSelectChange(e); setTradeType(e.target.value) }} select size="small">

                    <MenuItem value="">همه سفارش‌ها</MenuItem>

                    <MenuItem value="market">خرید</MenuItem>

                    <MenuItem value="limit">فروش</MenuItem>

                </Select>

            </Grid>

            <Grid item className="d-flex">

                <label className="text-white ms-2 mt-2" style={{ fontSize: 'smaller' }}>نوع سفارش</label>

                <Select name="trade_type" value={tradeType} input={<BootstrapInput />} displayEmpty onChange={e => { handleSelectChange(e); setTradeType(e.target.value) }} select size="small">

                    <MenuItem value="">همه سفارش‌ها</MenuItem>

                    <MenuItem value="market">سریع</MenuItem>

                    <MenuItem value="limit">محدود</MenuItem>

                </Select>

            </Grid> */}

            {/* <Grid xs={3} item className="" >

                <TextField

                    value={nationalCode}

                    type="search"

                    onChange={e => setNationalCode(e.target.value)}

                    name="national_code"

                    size="small"

                    placeholder=" جست و جو نام یا کد ملی"

                    InputProps={{

                        endAdornment: (

                            <InputAdornment>

                                <IconButton onClick={() => props.setParams(state => ({ ...state, national_code: nationalCode }))}>

                                    <SearchIcon />

                                </IconButton>

                            </InputAdornment>

                        )

                    }}

                />

            </Grid> */}

            <Grid item className="d-flex">

            <label className="text-white ms-2 mt-2" style={{ fontSize: 'smaller' }}>تاریخ</label>

            <ConfigProvider locale={fa_IR} direction="rtl">

                <DatePickerJalali.RangePicker  onChange={onChange}  className="form-control h-75 my-auto rounded-1" format={dateFormat} dropdownClassName="datePickerDropDown" popupStyle={{ backgroundColor: "red" }} />

            </ConfigProvider>

            </Grid>

        </Grid>

    )
}

export default Filters
