import React from 'react';

import { makeStyles } from '@material-ui/styles';

import { Button, Dialog, DialogTitle, DialogActions, DialogContent } from '@material-ui/core';

import { DataGrid, useGridSlotComponentProps } from '@material-ui/data-grid';

import { Pagination } from '@material-ui/lab';

import { useFetchApi } from "../../utils/hooks";

import { USERS_ORDERS ,USER_MESSAGES ,IAM_APP} from '../../utils/constants';

import { timeToStr, UrlQuery } from '../../utils/utils';

import { useHistory, useParams } from 'react-router-dom';

import { Mail } from "@material-ui/icons";

import Filters from './filters';





const useStyles = makeStyles({

  root: {

    // display: 'flex',

    margin: '0 auto',

  },

});



function CustomPagination() {

  const { state, apiRef } = useGridSlotComponentProps();

  const classes = useStyles();



  return (

    <Pagination

      className={classes.root}

      shape="rounded"

      color="primary"

      // variant="contained"

      count={state.pagination.pageCount}

      page={state.pagination.page + 1}

      onChange={(event, value) => apiRef.current.setPage(value - 1)}

    />

  );

}



const rows = [

  {

    id: 1,

    tracking_code: "412515",

    owner_id: "پیام سری ۲",

    national_code: "55457575",

    origin_wallet: "btc",

    quantity: "2.3",

    dest_wallet: "تست تستیانی",

    action: "approve",

    status: "in_progress",

  },

  {

    id: 2,

    tracking_code: "412515",

    owner_id: "پیام سری",

    national_code: "55457575",

    origin_wallet: "تست تستیانی",

    date: "2.3",

    dest_wallet: "علی تستی",

    action: "approve",

    status: "in_progress",

  },

  {

    id: 3,

    tracking_code: "412515",

    owner_id: "fjasfkljas",

    national_code: "55457575",

    origin_wallet: "btc",

    quantity: "2.3",

    dest_wallet: "",

    action: "approve",

    status: "in_progress",

  },

]





const MessageModal = (props) => {



  return <Dialog

    maxWidth="md"

    fullWidth

    //   onEntering={handleEntering}

    aria-labelledby="confirmation-dialog-title"

    open={props.modalOpen}

  >

    <DialogTitle className="">

      <div className="d-flex justify-content-end">

        <p className="font_title_name me-1" >مشاهده پیام</p>

        <img src="/assests/drawer/Polygon.svg" />

      </div>

    </DialogTitle>

    <DialogContent >

      <div className="border border-light rounded p-3">

        <div className="d-flex justify-content-between px-3 text-muted">

          <span> 99 / 01 / 22 - 05 : 32</span>

          <span>احراز هویت</span>

        </div>

        <p className="text-end p-3">

          احراز هویت شما به دلیل ناواضح بودن عکس ارسالی ناموفق انجام شد

        </p>

      </div>

    </DialogContent>

    <DialogActions className="justify-content-start px-4 py-2">

      <div>

        <Button autoFocus onClick={() => props.setModalOpen(false)} variant="contained" color="primary">

          بستن

        </Button>

      </div>

    </DialogActions>

  </Dialog>

}





function MessagesDatagrid(props) {



  const history = useHistory()



  const columns = [

    {

      field: 'id',

      headerName: 'شماره',

      flex: 1,

      type: 'string',

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      // renderCell: (values) => `${values.row?.tracking_code.slice(0, 8)}`

    },

    {

      field: 'owner_id',

      headerName: 'عنوان پیام',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      // renderCell: (values) => (

      //   <div className="d-flex">

      //        <span>{values.row?.origin_wallet} - {values.row?.dest_wallet}</span>

      //   </div>

      // )

    },

    {

      field: 'origin_wallet',

      headerName: 'دریافت کننده',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      // renderCell: (values) => (

      //   <div className="d-flex">

      //        {values.row?.trade_type === "sell" ? <span className="text-danger">فروش</span> : <span className="text-success">خرید</span>}

      //   </div>

      // )

    },

    {

      field: 'dest_wallet',

      headerName: 'ارسال کننده',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      // renderCell: (values) => (

      //   <div className="d-flex">

      //        {values.row?.trade_type === "limit" ? "محدود" : "سریع"}

      //   </div>

      // )

    },

    {

      field: 'date',

      headerName: 'تاریخ',

      flex: 1,

      sortable: false,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      align: 'center',

      // renderCell: (values) => (

      //   <div className="d-flex">

      //        {values.row?.trade_type === "limit" ? "محدود" : "سریع"}

      //   </div>

      // )

    },

    {

      field: 'status',

      headerName: 'عملیات',

      // description: 'This column has a value getter and is not sortable.',

      sortable: false,

      align: 'center',

      flex: 0.75,

      headerClassName: 'grid-header',

      headerAlign: 'center',

      renderCell: (values) => <Button variant="contained" color="primary" onClick={() => setModalOpen(true)} className="ms-2">مشاهده پیام</Button>



    }

  ];



  const [modalOpen, setModalOpen] = React.useState(false);

  const [params, setParams] = React.useState({ order: "-updated_at", size: 10, page: 0 })

  const [{ data, isLoading, hasError }, doFetch] = useFetchApi(undefined, [])



  React.useEffect(() => {

    doFetch("GET", IAM_APP , UrlQuery(USER_MESSAGES, {...params}) )

  }, [params])



  return (

    <div className="content">

      <div className=" d-flex align-items-center justify-content-between w-auto mx-auto pt-2">

        <div className="d-flex">

          <img src="/assests/drawer/Polygon.svg" />

          <p className="font_title_name me-1" style={{ minWidth: "150px" }}>تاریخچه پیام‌ها</p>

        </div>

        <Filters params={params} setParams={setParams} />

        <Button

          variant="contained"

          className="ms-4"

          style={{ minWidth: "160px" }}

          onClick={() => history.push("/messages/new")}

          color="secondary"

          startIcon={<Mail className="ms-2" />}

        > ارسال پیام جدید</Button>

      </div>

      <MessageModal modalOpen={modalOpen} setModalOpen={setModalOpen} />

      <div className="d-flex px-3">

        <div style={{ flexGrow: 1, width: "100%", height: "100%" }}>

          <DataGrid

            style={{ border: 0 }}

            autoHeight

            // rows={data?.items ?? []}

            rows={rows}

            loading={isLoading}

            columns={columns}

            getRowClassName={

              params => 'grid-content'

            }

            headerHeight={50}

            rowHeight={52}

            isRowSelectable={false}

            components={{

              Pagination: CustomPagination

            }}

            pageSize={10}

            rowCount={data?.total}

            paginationMode="server"

            onPageChange={(param) => doFetch("GET", UrlQuery(USERS_ORDERS, { ...params, page: param }))}

            disableSelectionOnClick

            disableColumnMenu

          />

        </div>

      </div>

    </div>

  )

}



export default MessagesDatagrid
