import React from 'react';
import Editor from './editor';
import { Typography, Container, Box } from '@material-ui/core';
import { createReactEditorJS } from "react-editor-js";
import { EDITOR_JS_TOOLS } from './editor/constants';

const ReactEditorJS = createReactEditorJS();

const AboutUs = () => {
    const editorJS = React.useRef(null)

const handleInitialize = React.useCallback((instance) => {
  editorJS.current = instance
}, [])

const handleSave = React.useCallback(() => {
  const savedData =  editorJS.current.save();
}, [])
    return (
        <div className="content">
        <div className=" d-flex align-items-center justify-content-between w-auto mx-auto pt-2">
          <div className="d-flex">
            <img src="/assests/drawer/Polygon.svg" />
            <p className="font_title_name me-1" style={{minWidth: "150px"}}>صفحه درباره ما</p>
          </div>
            </div>
            <Container
        style={{ backgroundColor: '#d4ecff', minHeight: "100vh" }}
        maxWidth="xl">
        <Box p={5}>
          <Box>
            <Typography variant="h6" component="span">
              EditorJS With React
        </Typography>
          </Box>
          <Box
            mt={2}
            style={{
              backgroundColor: '#ffffff',
              border: '1px solid #cccccc'
            }}>
            {/* <Editor 
            header_text="درباره ما"
            /> */}
            <ReactEditorJS
                tools={EDITOR_JS_TOOLS}
                onInitialize={handleInitialize}
                onChange={() => handleSave()}
                defaultValue={{
                    time: 1635603431943,
                    blocks: [
                      {
                        id: "sheNwCUP5A",
                        type: "header",
                        data: {
                          text: "Editor.js",
                          level: 2
                        }
                      },            {
                        id: "12iM3lqzcm",
                        type: "paragraph",
                        data: {
                          text:
                            "Hey. Meet the new Editor. On this page you can see it in action — try to edit this text."
                        }
                      },]
                    }}
            />
          </Box>
        </Box>
      </Container>
        </div>
    );
}

export default AboutUs;
