import React, {useState} from 'react'
import { TextField, MenuItem, Grid, IconButton, InputAdornment, InputBase, withStyles, Select } from "@material-ui/core";
import SearchIcon from "@material-ui/icons/Search";


function Filters(props) {

    const [mobile, setMobile] = useState('')

    const [originWallet, setOriginWallet] = useState();

    const handleSelectChange = (e) => {
        props.setParams( state => ({...state, [e.target.name]: e.target.value}) );
    }


    const BootstrapInput = withStyles((theme) => ({

        root: {

            //   'label + &': {

            //     marginTop: theme.spacing(3),

            //   },

        },

        input: {

            borderRadius: 4,

            position: 'relative',

            backgroundColor: 'white',

            border: '1px solid #ced4da',

            fontSize: 'smaller',

            padding: '5px 12px 5px 12px',

            color: 'black',

            transition: theme.transitions.create(['border-color', 'box-shadow']),

            // Use the system font instead of the default Roboto font.

            fontFamily: [

                "IranSans",

            ].join(','),

            '&:focus': {

                borderRadius: 4,

                borderColor: '#80bdff',

                boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',

                backgroundColor: 'white'


            },

        },

    }))(InputBase);

    React.useEffect( () => {
        if (mobile === ''){
            props.setParams( state => state)
        }
    }, [mobile])

    return (
        <Grid className="mb-3 ps-2" justify="flex-end" container spacing={1}>
            <Grid item className="d-flex">
                <label className="text-white ms-2 mt-2" style={{ fontSize: 'smaller' }}>دسته‌بندی براساس</label>
                <Select name="origin_wallet" value={originWallet} displayEmpty onChange={ e => {handleSelectChange(e); setOriginWallet(e.target.value)}} select size="small">
                    <MenuItem value="">همه</MenuItem>
                    <MenuItem value="irr">ریال</MenuItem>
                    <MenuItem value="btc">بیت کوین</MenuItem>
                </Select> 
            </Grid>
            <Grid item ><TextField 
                            value={mobile} 
                            type="search" 
                            onChange={ e => setMobile(e.target.value)} 
                            name="mobile__contain" 
                            size="small" 
                            placeholder="جست و جو"
                            InputProps={{
                                endAdornment: (
                                  <InputAdornment>
                                    <IconButton onClick={ () => props.setParams( state => ({...state, mobile__contain: mobile}))}>
                                      <SearchIcon />
                                    </IconButton>
                                  </InputAdornment>
                                )
                              }}
                        />
            </Grid>
        </Grid>
    )
}

export default Filters
