import React from 'react';

import { Link } from "react-router-dom";

import { Field, Formik, Form } from "formik";

import { Grid, CircularProgress, Button, Checkbox, FormControlLabel, Select , MenuItem, withStyles } from '@material-ui/core';

import InputBase from '@material-ui/core/InputBase';

import { USERS_LIST , IAM_APP} from "../../utils/constants"

import { useFetchApi } from '../../utils/hooks';

import { UrlQuery } from '../../utils/utils';


function NewMessage(props) {



    let loginLoading = true



    const [checked, setChecked] = React.useState(false)


    const BootstrapInput = withStyles((theme) => ({

        root: {

            //   'label + &': {

            //     marginTop: theme.spacing(3),

            //   },
            width: "inherit !important",


        },

        input: {

            borderRadius: 4,

            position: 'relative',

            backgroundColor: 'white',

            border: '1px solid #ced4da',

            fontSize: 'smaller',

            padding: '5px 12px 5px 12px',

            color: 'black',

            transition: theme.transitions.create(['border-color', 'box-shadow']),

            direction: "rtl !important",
            // Use the system font instead of the default Roboto font.

            fontFamily: [

                '-apple-system',

                'BlinkMacSystemFont',

                '"Segoe UI"',

                'Roboto',

                '"Helvetica Neue"',

                'Arial',

                'sans-serif',

                '"Apple Color Emoji"',

                '"Segoe UI Emoji"',

                '"Segoe UI Symbol"',

            ].join(','),

            '&:focus': {

                borderRadius: 4,

                borderColor: '#80bdff',

                boxShadow: '0 0 0 0.2rem rgba(0,123,255,.25)',

                backgroundColor: 'white'

            },

        },

    }))(InputBase);




    const [params, setParams] = React.useState({ size: 10, page: 0 })

    const [{ data:userNames, isLoading:userNamesIsLoading, hasError:userNamesHasError }, doFetch] = useFetchApi(undefined, [])

    const [{ data:submitMessage, isLoading:submitMessageIsLoading, hasError:submitMessageHasError }, doFetchSubmit] = useFetchApi(undefined, [])



    const handleSubmit= () => {

        doFetchSubmit("POST", IAM_APP , UrlQuery(`${USER_MESSAGES}/send_message/`, {...params}) )

    }


    React.useEffect(() => {

      doFetch("GET", IAM_APP , UrlQuery(USERS_LIST, {...params}) )

    }, [params])


    return (

        <div className="content mt-5 p-4">

            <div className="d-flex justify-content-between">

                <div className="d-flex w-25">

                    <img src="/assests/drawer/Polygon.svg" />

                    <p className="font_title_name me-1">ارسال پیام </p>

                </div>

                <Link className="text-decoration-none" style={{ color: "#10D078" }} to={'/messages'} >بازگشت به تاریخچه پیام‌ها {`->`} </Link>

            </div>

            <div className="mt-4">

                <Formik

                    initialValues={{ account_number: '', tracking_code: '' }}

                    enableReinitialize={true}

                // validate={this.validate}

                onSubmit={handleSubmit}

                >

                    <Form>

                        <div style={{ flexGrow: 1 }} >

                            <Grid container spacing={3} justify="space-between" className="p-3">

                                <Grid item md={5}>

                                    <div className="d-flex w-100" style={{ maxHeight: "34px" }}>

                                        <label className="text-end text-white ms-4" style={{ minWidth: "90px" }} htmlFor="account_number">کاربر</label>

                                        {/* <Field

                                            type="text"

                                            name="account_number"

                                            placeholder=""

                                            required

                                            className="form-control bg_input input_text"

                                        /> */}
                                        {console.log("data",userNames)}

                                        <Select name="account_number" input={<BootstrapInput />} displayEmpty  select size="small">

                                            {userNames?.items?.map((item,index) => {

                                                return <MenuItem value="">{item.credentials.first_name} {item.credentials.last_name}</MenuItem>

                                            })}

                                        </Select>

                                    </div>

                                </Grid>

                            </Grid>

                            <Grid container >

                                <Grid item md={1}></Grid>

                                <Grid item md={5} className="">

                                    {/* <Checkbox label="انتخاب همه کاربران" value={checked} size="small" color="primary" onClick={ _ => setChecked( state => !state) } /> */}

                                    <FormControlLabel

                                        control={<Checkbox checked={checked} color="primary" onChange={_ => setChecked(state => !state)} name="checkedA" />}

                                        label="انتخاب همه کاربران"

                                        className="text-white me-0"

                                    />

                                </Grid>

                            </Grid>

                            <Grid container spacing={3} justify="space-between" className="p-3">

                                <Grid item md={5}>

                                    <div className="d-flex w-100" style={{ maxHeight: "34px" }}>

                                        <label className="text-end text-white fs-6 ms-4" style={{ minWidth: "90px" }} htmlFor="amount">عنوان پیام</label>

                                        <Field

                                            type="text"

                                            name="amount"

                                            placeholder=""

                                            required

                                            className="form-control bg_input input_text"

                                        />

                                    </div>

                                </Grid>

                            </Grid>

                            <Grid container spacing={3} justify="space-between" className="p-3">

                                <Grid item md>

                                    <div className="d-flex w-100">

                                        <label className="text-end text-white fs-6 ms-4" style={{ minWidth: "90px" }} htmlFor="amount">متن پیام</label>

                                        <Field

                                            as="textarea"

                                            name="amount"

                                            placeholder="متن پیام را بنویسید"

                                            required

                                            className="form-control bg_input input_text"

                                        />

                                    </div>

                                </Grid>

                            </Grid>

                            {/* { Boolean(hasError) && <p className="text-danger">{hasError}</p> } */}

                            <div className="mt-4 pt-3 ms-5 ps-2 d-flex justify-content-end">

                                <Button disabled={loginLoading} variant="contained" type="submit" color="primary" >

                                    ارسال پیام

                                    {loginLoading && <CircularProgress size={24} className="position-absolute" />}

                                </Button>

                            </div>

                        </div>

                    </Form>

                </Formik>

            </div>

        </div>

    )

}



export default NewMessage
